from pybricks.hubs import PrimeHub
from pybricks.parameters import Button, Color, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub()
motorEsquerda = Motor(Port.D, Direction.COUNTERCLOCKWISE)
motorDireita = Motor(Port.B, Direction.CLOCKWISE)
robo = DriveBase(motorEsquerda, motorDireita, 43.2, 130)
motorGarra = Motor(Port.F, Direction.COUNTERCLOCKWISE)

# Initialize variables.
missao = 1

def menu():
    global missao
    prime_hub.display.number(missao)
    motorGarra.control.limits(acceleration=2000)
    robo.settings(straight_speed=500)
    robo.settings(straight_acceleration=500)
    robo.settings(turn_rate=300)
    robo.settings(turn_acceleration=400)
    if Button.LEFT in prime_hub.buttons.pressed():
        prime_hub.speaker.beep(880, 120)
        if missao > 1:
            missao = missao - 1
        wait(200)
    if Button.RIGHT in prime_hub.buttons.pressed():
        prime_hub.speaker.beep(880, 120)
        if missao < 4:
            missao = missao + 1
        wait(200)
    if Button.CENTER in prime_hub.buttons.pressed():
        prime_hub.speaker.beep(784, 120)
        prime_hub.speaker.beep(988, 120)
        prime_hub.speaker.beep(1175, 150)
        if missao == 1:
            Missao1()
        elif missao == 2:
            Missao2()
        elif missao == 3:
            MIssao3()
        else:
            Missao4()
        prime_hub.speaker.beep(1047, 200)
        wait(50)
        prime_hub.speaker.beep(1319, 200)

def testeBateria():
    if prime_hub.battery.voltage() >= 8200:
        prime_hub.light.on(Color.GREEN)
    elif 7500 <= prime_hub.battery.voltage() < 8200:
        prime_hub.light.on(Color.GREEN)
    else:
        prime_hub.light.on(Color.RED)

def Missao1():
    robo.straight(350)
    robo.turn(60)
    robo.straight(510)
    robo.turn(27)
    robo.straight(170)
    robo.turn(-42)
    motorGarra.run_target(2000, 93)
    robo.settings(straight_speed=200)
    robo.straight(100, then=Stop.COAST)
    wait(1500)
    robo.settings(straight_speed=700)
    robo.straight(-70)
    robo.turn(-50)
    robo.straight(-500)
    robo.turn(120)
    robo.straight(15)
    robo.turn(15)
    robo.straight(70)
    motorGarra.run_target(50, 0)
    robo.turn(-10)
    robo.straight(-350)
    robo.turn(-45)
    robo.settings(straight_speed=700)
    robo.settings(straight_acceleration=1200)
    robo.straight(-550)
    robo.brake()

def Missao2():
    robo.straight(250)
    motorGarra.run_target(2000, 70)
    robo.turn(-32)
    motorGarra.run_target(2000, 55)
    robo.straight(170)
    robo.straight(-30)
    robo.turn(27)
    robo.straight(140)
    robo.straight(-60)
    motorGarra.run_target(2000, 75)
    robo.turn(20)
    robo.turn(-50)
    robo.settings(straight_speed=200)
    robo.straight(280)
    robo.turn(20)
    robo.straight(25)
    motorGarra.run_target(2000, 0)
    robo.turn(13)
    robo.settings(straight_speed=700)
    robo.straight(-600)
    motorGarra.run_target(100, 75)
    robo.settings(straight_speed=700)
    robo.settings(turn_rate=500)
    robo.straight(-130)
    robo.turn(-90)
    motorGarra.run_target(2000, 0)
    robo.straight(-300)
    robo.brake()

def MIssao3():
    robo.straight(350)
    robo.turn(60)
    robo.straight(530)
    robo.turn(37)
    motorGarra.run_target(2000, 72)
    robo.settings(straight_speed=400)
    robo.straight(279)
    motorGarra.run_target(2000, 0)
    robo.straight(-50)
    robo.settings(straight_speed=700)
    robo.turn(-30)
    robo.straight(-300)
    robo.turn(30)
    robo.straight(-80)
    motorGarra.run_target(100, 75)
    robo.settings(straight_acceleration=800)
    robo.straight(-280)
    robo.turn(-88)
    motorGarra.run_target(2000, 0)
    robo.straight(-600)
    robo.brake()

def Missao4():
    robo.straight(350)
    robo.turn(60)
    robo.straight(550)
    robo.turn(58)
    motorGarra.run_target(2000, 74)
    robo.straight(270)
    motorGarra.run_target(2000, 0)
    robo.straight(-30)
    robo.turn(16)
    robo.straight(-460)
    robo.turn(-43)
    robo.straight(-200)
    motorGarra.run_target(100, 75)
    robo.straight(-210)
    motorGarra.run_target(2000, 0)
    robo.brake()


# The main program starts here.
prime_hub.system.set_stop_button(Button.BLUETOOTH)
testeBateria()
motorGarra.run(-200)
wait(500)
motorGarra.hold()
motorGarra.reset_angle(0)
robo.use_gyro(True)
prime_hub.speaker.beep(880, 120)
wait(40)
prime_hub.speaker.beep(1320, 120)
wait(40)
prime_hub.speaker.beep(1760, 180)
while True:
    menu()
